mapper.select:
select ,c.name classname, m.`name` majorname, t1.`area_name` shengname, t2.`area_name` cityname, t3.`area_name` xianname FROM student s JOIN class c ON s.classid=c.id JOIN major m ON s.`majorid`=m.`id` LEFT JOIN think_areas t1 ON s.`sheng`=t1.`area_id` LEFT JOIN think_areas t2 ON s.`city`=t2.`area_id` LEFT JOIN think_areas t3 ON s.`xian`=t3.`area_id`
and s.name like concat('%',#{student.name},'%')
and s.sex=#{student.sex}
and s.sheng=#{student.sheng}
and s.city=#{student.city}
and s.xian=#{student.xian}
and c.teacherid=#{teacherid} and c.id=s.classid

select ,c.name classname, m.`name` majorname, t1.`area_name` shengname, t2.`area_name` cityname, t3.`area_name` xianname FROM student s JOIN class c ON s.classid=c.id JOIN major m ON s.`majorid`=m.`id` LEFT JOIN think_areas t1 ON s.`sheng`=t1.`area_id` LEFT JOIN think_areas t2 ON s.`city`=t2.`area_id` LEFT JOIN think_areas t3 ON s.`xian`=t3.`area_id`
and s.name like concat('%',#{student.name},'%')
and s.sex=#{student.sex}
and s.sheng=#{student.sheng}
and s.city=#{student.city}
and s.xian=#{student.xian}
